SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajuste_atributos_web (ie_tipo_guia_p text) AS $body$
DECLARE


qt_registro_w			bigint;
nr_seq_restricao_funcao_w	bigint;
nr_seq_atributo_w		bigint;


BEGIN


if (ie_tipo_guia_p IS NOT NULL AND ie_tipo_guia_p::text <> '') then

	select	count(1)
	into STRICT	qt_registro_w
	from	user_tab_columns
	where	table_name 	= 'PLS_RESTRICAO_FUNCAO_WEB';

	if (qt_registro_w > 0) then

		select  max(nr_sequencia)
		into STRICT	nr_seq_restricao_funcao_w
		from	pls_restricao_funcao_web
		where	ie_tipo_guia = ie_tipo_guia_p
		and	ie_funcionalidade = 3
		and	ie_situacao = 'A';

		if (coalesce(nr_seq_restricao_funcao_w::text, '') = '') then

			select	nextval('pls_restricao_funcao_web_seq')
			into STRICT	nr_seq_restricao_funcao_w
			;

			insert into pls_restricao_funcao_web(nr_sequencia, ie_funcionalidade, dt_atualizacao,
				     nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				     ie_tipo_guia, ie_situacao )
			      values ( nr_seq_restricao_funcao_w, 3, clock_timestamp(),
				     'aasestari', clock_timestamp(), 'aasestari',
				     ie_tipo_guia_p, 'A');
		end if;

		select	max(nr_sequencia)
		into STRICT	nr_seq_atributo_w
		from	pls_atributos_web
		where	nm_atributo = 'VL_MATERIAL_IMP'
		and	nm_tabela = 'PLS_CONTA_MAT';

		if (nr_seq_atributo_w IS NOT NULL AND nr_seq_atributo_w::text <> '') then

			insert into pls_restricao_atrib_web
			     values (nextval('pls_restricao_atrib_web_seq'), nr_seq_restricao_funcao_w, nr_seq_atributo_w,
				    clock_timestamp(), 'aasestari', clock_timestamp(),
				    'aasestari', 'S');

		end if;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_ajuste_atributos_web (ie_tipo_guia_p text) FROM PUBLIC;

