SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajuste_attr_req_web () AS $body$
DECLARE


qt_registro_w				bigint;
nr_seq_restricao_funcao_w	bigint;
nr_seq_atributo_w			bigint;
cd_cgc_w					varchar(14);


BEGIN

select		max(cd_cgc_outorgante)
into STRICT		cd_cgc_w
from		pls_outorgante;

if (cd_cgc_w = '45100138000109') then --Rio preto
	select	count(1)
	into STRICT	qt_registro_w
	from	PLS_ATRIBUTOS_WEB
	where	nr_sequencia in (23,24,25);

	if (qt_registro_w = 3) then

		select	count(1)
		into STRICT	qt_registro_w
		from	pls_regra_atributo_web
		where	nr_seq_atributo_web in (23,24,25);

		if (qt_registro_w = 0) then

			insert into pls_regra_atributo_web(nr_sequencia, nr_seq_atributo_web, dt_atualizacao,
											   nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
											   ds_info_label_atributo)
									values (nextval('pls_regra_atributo_web_seq'), 23, clock_timestamp(),
											   'aasestari', clock_timestamp(), 'aasestari',
											   'Opção utilizada para beneficiários que apresentem cartão da Unimed Rio Preto (cartão de início 0030), inclusive Usina Generalco e '||
											   'Ministério da Aeronáutica (G.A.B.) e também beneficiários de Intercâmbio (cartão de início diferente de 0030).');

			insert into pls_regra_atributo_web(nr_sequencia, nr_seq_atributo_web, dt_atualizacao,
											   nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
											   ds_info_label_atributo)
									values (nextval('pls_regra_atributo_web_seq'), 24, clock_timestamp(),
											   'aasestari', clock_timestamp(), 'aasestari',
											   'Opção utilizada para contratos do antigo ''Solicitação de Atendimento'', formado por beneficiários que não possuem cartão Unimed, como as empresas:'||
											   ' Caixa Econômica Federal (C.E.F.), Economus e Fundação CESP (exceto Usina Generalco e Ministério da Aeronáutica (G.A.B.), que devem ser solicitados através da opção ''Normal'').');

			insert into pls_regra_atributo_web(nr_sequencia, nr_seq_atributo_web, dt_atualizacao,
											   nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
											   ds_info_label_atributo)
									values (nextval('pls_regra_atributo_web_seq'), 25, clock_timestamp(),
											   'aasestari', clock_timestamp(), 'aasestari',
											   'Opção utilizada para beneficiários de Intercâmbio (cartão de início diferente de 0030), que apresentem G.A.T. - Guia de Atendimento, emitida previamente pela'||
											   ' Unimed de Origem do beneficiário.');

			commit;

		end if;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_ajuste_attr_req_web () FROM PUBLIC;

