SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajuste_banco_mens () AS $body$
DECLARE


nr_seq_mensalidade_w		bigint;
nr_seq_pagador_w		bigint;
nr_seq_forma_cobranca_w		bigint;
cd_banco_w			integer;
cd_agencia_bancaria_w		varchar(8);
ie_digito_agencia_w		varchar(1);
cd_conta_w			varchar(20);
ie_digito_conta_w		varchar(2);
ie_endereco_boleto_w		varchar(4);
nr_seq_conta_banco_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_pagador
	from	pls_mensalidade
	where	coalesce(nr_seq_conta_banco::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_mensalidade_w,
	nr_seq_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	b.nr_seq_forma_cobranca,
		b.cd_banco,
		b.cd_agencia_bancaria,
		b.ie_digito_agencia,
		b.cd_conta,
		b.ie_digito_conta,
		a.ie_endereco_boleto,
		b.nr_seq_conta_banco
	into STRICT	nr_seq_forma_cobranca_w,
		cd_banco_w,
		cd_agencia_bancaria_w,
		ie_digito_agencia_w,
		cd_conta_w,
		ie_digito_conta_w,
		ie_endereco_boleto_w,
		nr_seq_conta_banco_w
	from	pls_contrato_pagador a,
		pls_contrato_pagador_fin b
	where	a.nr_sequencia	= b.nr_seq_pagador
	and	a.nr_sequencia	= nr_seq_pagador_w
	and	coalesce(b.dt_fim_vigencia::text, '') = '';

	if (nr_seq_conta_banco_w IS NOT NULL AND nr_seq_conta_banco_w::text <> '') then
		update	pls_mensalidade
		set	nr_seq_forma_cobranca	= nr_seq_forma_cobranca_w,
			cd_banco		= cd_banco_w,
			cd_agencia_bancaria	= cd_agencia_bancaria_w,
			ie_digito_agencia	= ie_digito_agencia_w,
			cd_conta		= cd_conta_w,
			ie_digito_conta		= ie_digito_conta_w,
			ie_endereco_boleto	= ie_endereco_boleto_w,
			nr_seq_conta_banco	= nr_seq_conta_banco_w
		where	nr_sequencia	= nr_seq_mensalidade_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
