SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajuste_dt_titulo_tps () AS $body$
DECLARE


nr_seq_taxa_saude_w		bigint;
dt_atualizacao_w		timestamp;
dt_geracao_titulo_w		timestamp;
qt_titulo_tps_w			integer;


C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_atualizacao
	from	pls_tps
	where	coalesce(dt_geracao_titulo::text, '') = '';

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_taxa_saude_w,
	dt_atualizacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select   count(*)
	into STRICT	 qt_titulo_tps_w
	from     titulo_pagar
	where    nr_seq_taxa_saude = nr_seq_taxa_saude_w
	and      ie_situacao <> 'C';

	if (qt_titulo_tps_w > 0) then
		update	pls_tps
		set 	dt_geracao_titulo = dt_atualizacao_w
		where	nr_sequencia = nr_seq_taxa_saude_w;

		commit;
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_ajuste_dt_titulo_tps () FROM PUBLIC;

