SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajuste_estab_analise () AS $body$
DECLARE


nr_seq_analise_w	bigint;
cd_estabelecimento_w	bigint;
qt_contador_w		bigint	:= 0;

/* Analises vinculadas a conta */

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	b.cd_estabelecimento
from	pls_conta b,
	pls_analise_conta a
where	a.nr_sequencia	= b.nr_seq_analise
and	coalesce(a.cd_estabelecimento::text, '') = ''

union all

select	a.nr_sequencia,
	c.cd_estabelecimento
from	pls_conta c,
	pls_conta_pos_estabelecido b,
	pls_analise_conta a
where	a.nr_sequencia	= b.nr_seq_analise
and	b.nr_seq_conta	= c.nr_sequencia
and	coalesce(a.cd_estabelecimento::text, '') = '';

/* Analises vinculadas a discussão */

c02 CURSOR FOR
SELECT	a.nr_sequencia,
	d.cd_estabelecimento
from	pls_lote_contestacao d,
	pls_lote_discussao c,
	pls_contestacao_discussao b,
	pls_analise_conta a
where	a.nr_sequencia	= b.nr_seq_analise
and	b.nr_seq_lote	= c.nr_sequencia
and	c.nr_seq_lote_contest = d.nr_sequencia
and	coalesce(a.cd_estabelecimento::text, '') = '';


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_analise_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	update	pls_analise_conta
	set	cd_estabelecimento	= cd_estabelecimento_w
	where	nr_sequencia		= nr_seq_analise_w
	and	coalesce(cd_estabelecimento::text, '') = '';

	qt_contador_w	:= qt_contador_w + 1;

	if (qt_contador_w > 300) then
		commit;
		qt_contador_w	:= 0;
	end if;
	end;
end loop;
close c01;

open c02;
loop
fetch c02 into
	nr_seq_analise_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	begin
	update	pls_analise_conta
	set	cd_estabelecimento	= cd_estabelecimento_w
	where	nr_sequencia		= nr_seq_analise_w;

	qt_contador_w	:= qt_contador_w + 1;

	if (qt_contador_w > 300) then
		commit;
		qt_contador_w	:= 0;
	end if;
	end;
end loop;
close c02;

select	max(a.cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	pls_outorgante a;

if (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') then
	/* Analises que não achou correspondencia, pegar estabelecimento da operadora */

	update	pls_analise_conta
	set	cd_estabelecimento	= cd_estabelecimento_w
	where	coalesce(cd_estabelecimento::text, '') = '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_ajuste_estab_analise () FROM PUBLIC;

