CREATE OR REPLACE PROCEDURE pls_baca_ajuste_fat_competenc () AS $body$
DECLARE


nr_seq_pls_fatura_w		pls_fatura.nr_sequencia%type;
dt_mesano_referencia_w		pls_lote_faturamento.dt_mesano_referencia%type;

C01 CURSOR FOR
	SELECT	x.nr_sequencia,
		l.dt_mesano_referencia
	from	pls_fatura		x,
		pls_lote_faturamento	l
	where	l.nr_sequencia	= x.nr_seq_lote
	and	coalesce(x.dt_mes_competencia::text, '') = '';

BEGIN
for r_C01_w in C01() loop
	update	pls_fatura
	set	dt_mes_competencia	= r_C01_w.dt_mesano_referencia
	where	nr_sequencia		= r_C01_w.nr_sequencia
	and	coalesce(dt_mes_competencia::text, '') = '';

	commit;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
