SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajuste_historico_seg () AS $body$
DECLARE


nr_seq_historico_w	bigint;
nr_seq_contrato_ant_w	bigint;
cd_pessoa_fisica_w	varchar(14);
nr_seq_segurado_w	bigint;
nr_seq_plano_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		(substr(a.ds_observacao,20,3))::numeric ,
		b.cd_pessoa_fisica
	from	pls_segurado_historico a,
		pls_segurado b
	where	a.nr_seq_segurado	= b.nr_sequencia
	and	a.ie_tipo_historico = '5'
	and (coalesce(a.nr_seq_plano_ant::text, '') = ''
	or	coalesce(a.nr_seq_contrato_ant::text, '') = '')
	and	coalesce(nr_seq_segurado_atual::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_historico_w,
	nr_seq_contrato_ant_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(a.nr_sequencia),
		max(a.nr_seq_plano)
	into STRICT	nr_seq_segurado_w,
		nr_seq_plano_w
	from	pls_segurado a
	where	a.nr_seq_contrato	= nr_seq_contrato_ant_w
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_w;

	update	pls_segurado_historico
	set	nr_seq_plano_ant	= nr_seq_plano_w,
		nr_seq_contrato_ant	= nr_seq_contrato_ant_w
	where	nr_sequencia	= nr_seq_historico_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
