CREATE OR REPLACE PROCEDURE pls_baca_ajuste_mat_conta ( nm_usuario_p text) AS $body$
DECLARE


cd_material_imp_w		varchar(20);
nr_seq_material_w		bigint;
nr_seq_conta_mat_w		bigint;

C01 CURSOR FOR
	SELECT	c.cd_material_imp,
		c.nr_sequencia
	from	pls_protocolo_conta a,
		pls_conta b,
		pls_conta_mat c
	where	a.nr_sequencia	= b.nr_seq_protocolo
	and	b.nr_sequencia	= c.nr_seq_conta
	and	a.nr_sequencia	in (	7388, 7385, 7380, 7368, 7367, 7345, 7344, 7334, 7332, 7317, 7316, 7302, 7303, 7291, 7287, 7286,
					7283, 7282, 7269, 7268, 7248, 7247, 7245, 7244, 7239, 7238, 7393, 7389, 7359, 7326, 7208, 7169);


BEGIN

open C01;
loop
fetch C01 into
	cd_material_imp_w,
	nr_seq_conta_mat_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_material_w
	from	pls_material
	where	nr_sequencia	= somente_numero(cd_material_imp_w);

	if (coalesce(nr_seq_material_w,0) <> 0) then
		update	pls_conta_mat
		set	nr_seq_material	= nr_seq_material_w,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_conta_mat_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
