CREATE OR REPLACE PROCEDURE pls_baca_ajuste_reaj_nao_aplic () AS $body$
DECLARE


qt_reajuste_nao_aplic_w		bigint;
nr_seq_reajuste_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_reajuste;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_reajuste_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_reajuste_nao_aplic_w
	from	pls_reajuste_historico
	where	nr_seq_reajuste	= nr_seq_reajuste_w;

	if (qt_reajuste_nao_aplic_w > 0) then
		update	pls_reajuste
		set	ie_reaj_nao_aplicado	= 'S'
		where	nr_sequencia	= nr_seq_reajuste_w;
	else
		update	pls_reajuste
		set	ie_reaj_nao_aplicado	= 'N'
		where	nr_sequencia	= nr_seq_reajuste_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
