CREATE OR REPLACE PROCEDURE pls_baca_ajuste_regra_conv_mat () AS $body$
BEGIN

	-- Se as regras tem os 3 campos, nulos, então converte aqui para tratar apenas o ie_xml. Manterá comportamento padrão da mesma
	update	pls_conversao_mat
	set		ie_xml = 'S',
			ie_digitacao_portal = 'N',
			ie_complemento = 'N'
	where	coalesce(ie_xml::text, '') = ''
	and		coalesce(ie_digitacao_portal::text, '') = ''
	and 	coalesce(ie_complemento::text, '') = ''
	and (ie_envio_receb = 'R' or coalesce(ie_envio_receb::text, '') = '');


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
