CREATE OR REPLACE PROCEDURE pls_baca_ajuste_regra_emissor () AS $body$
DECLARE


nr_seq_campo_carteira_w		bigint;
nr_seq_regra_carteira_w		bigint;
nr_seq_emissor_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_regra_carteira
	from	pls_regra_carteira_campo
	where	coalesce(nr_seq_emissor::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_campo_carteira_w,
	nr_seq_regra_carteira_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_seq_emissor)
	into STRICT	nr_seq_emissor_w
	from	pls_regra_carteira
	where	nr_sequencia	= nr_seq_regra_carteira_w;

	if (coalesce(nr_seq_emissor_w,0) > 0) then
		update	pls_regra_carteira_campo
		set	nr_seq_emissor	= nr_seq_emissor_w
		where	nr_sequencia	= nr_seq_campo_carteira_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
