SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajuste_situacao_mat () AS $body$
DECLARE


qt_registro_w	bigint	:= 0;
nr_sequencia_w	bigint;
dt_inclusao_w	timestamp;
dt_exclusao_w	timestamp;
ie_situacao_w	varchar(1);

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	trunc(coalesce(a.dt_atualizacao_nrec,a.dt_atualizacao),'dd'),
	trunc(a.dt_atualizacao,'dd'),
	coalesce(a.ie_situacao,'A')
from	pls_material a
where	not exists (select	1
			from	pls_material x
			where	(x.dt_inclusao IS NOT NULL AND x.dt_inclusao::text <> ''));


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	dt_inclusao_w,
	dt_exclusao_w,
	ie_situacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	qt_registro_w	:= qt_registro_w + 1;

	if (ie_situacao_w	= 'A') then
		dt_exclusao_w	:= null;
	end if;

	update	pls_material
	set	dt_inclusao	= dt_inclusao_w,
		dt_exclusao	= dt_exclusao_w
	where	nr_sequencia	= nr_sequencia_w;

	if (qt_registro_w > 500) then
		commit;
		qt_registro_w	:= 0;
	end if;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
