SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajuste_tipo_valor () AS $body$
DECLARE

 
nr_seq_segurado_w	bigint;
dt_reajuste_w		timestamp;
dt_liberacao_w		timestamp;
nr_seq_segurado_preco_w	bigint;
vl_preco_atual_w	double precision;
qt_idade_w		smallint;
nr_seq_tabela_preco_w	bigint;
vl_preco_inicial_w	double precision;
vl_preco_final_w	double precision;
ds_erro_w		varchar(255);

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		d.dt_reajuste, 
		obter_idade(pls_obter_dados_segurado(a.nr_sequencia,'NAS'),clock_timestamp(),'A'), 
		b.nr_sequencia 
	from	pls_segurado a, 
		pls_tabela_preco b, 
		pls_reajuste_tabela c, 
		pls_reajuste d 
	where	a.nr_seq_tabela = b.nr_sequencia 
	and	b.nr_sequencia = c.nr_seq_tabela 
	and	d.nr_sequencia = c.nr_seq_reajuste 
	and	exists (SELECT	1 
			from	pls_segurado_preco x 
			where	(x.nr_seq_reajuste IS NOT NULL AND x.nr_seq_reajuste::text <> '') 
			and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '') 
			and	x.nr_seq_segurado    = a.nr_sequencia);


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_segurado_w, 
	dt_reajuste_w, 
	qt_idade_w, 
	nr_seq_tabela_preco_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	coalesce(max(a.nr_sequencia),0) 
	into STRICT	nr_seq_segurado_preco_w 
	from	pls_segurado_preco a, 
		pls_lote_reaj_segurado b 
	where	a.nr_seq_lote	= b.nr_sequencia 
	and	a.nr_seq_segurado	= nr_seq_segurado_w;
	 
	if (nr_seq_segurado_preco_w > 0) then 
		/* Data do último reajuste aplicado no beneficiário*/
 
		select	a.dt_liberacao, 
			a.vl_preco_atual 
		into STRICT	dt_liberacao_w, 
			vl_preco_atual_w 
		from	pls_segurado_preco a, 
			pls_lote_reaj_segurado b 
		where	a.nr_seq_lote	= b.nr_sequencia 
		and	a.nr_seq_segurado	= nr_seq_segurado_w 
		and	a.nr_sequencia	= nr_seq_segurado_preco_w;
	end if;
	 
	if (coalesce(nr_seq_tabela_preco_w,0) <> 0) and (dt_liberacao_w < dt_reajuste_w) then 
		begin 
		select	vl_preco_inicial, 
			vl_preco_atual 
		into STRICT	vl_preco_inicial_w, 
			vl_preco_final_w 
		from	pls_plano_preco 
		where	nr_seq_tabela	= nr_seq_tabela_preco_w 
		and	qt_idade_w between qt_idade_inicial and qt_idade_final;
		exception 
		when others then 
			ds_erro_w := '1';
		end;
		 
		if (vl_preco_inicial_w = vl_preco_atual_w) then 
			update	pls_segurado 
			set	ie_tipo_valor = 'I', 
				dt_atualizacao = clock_timestamp(), 
				nm_usuario = 'TASY' 
			where	nr_sequencia	= nr_seq_segurado_w;
		end if;
	end if;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_ajuste_tipo_valor () FROM PUBLIC;

