SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_alterar_fone_mascara ( nm_usuario_p text) AS $body$
DECLARE


nr_telefone_w			varchar(15);
nr_seq_local_atend_w		bigint;
nr_tamanho_campo_w		smallint;

C01 CURSOR FOR
	SELECT	nr_telefone,
		nr_sequencia
	from	local_atendimento_medico
	order by 2;


BEGIN

begin
	open C01;
	loop
	fetch C01 into
		nr_telefone_w,
		nr_seq_local_atend_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_tamanho_campo_w := length(trim(both nr_telefone_w));
		if (nr_tamanho_campo_w = 8) then

			update	local_atendimento_medico
			set	nr_telefone = '(  )'||substr(nr_telefone_w,1,4)||'-'||substr(nr_telefone_w,5,4),
				nm_usuario = nm_usuario_p
			where	nr_sequencia = nr_seq_local_atend_w;

		elsif (nr_tamanho_campo_w = 10) then

			update	local_atendimento_medico
			set	nr_telefone = '('||substr(nr_telefone_w,1,2)||')'||substr(nr_telefone_w,3,4)||'-'||substr(nr_telefone_w,7,4),
				nm_usuario = nm_usuario_p
			where	nr_sequencia = nr_seq_local_atend_w;
		end if;
		end;
	end loop;
	close C01;
exception
when others then
	nr_tamanho_campo_w := 0;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_alterar_fone_mascara ( nm_usuario_p text) FROM PUBLIC;

