SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_alterar_senha_md5 () AS $body$
DECLARE


nr_seq_estipulante_w			bigint;
ds_senha_w				varchar(32);
ds_senha_nova_w				varchar(32);
nr_seq_prestador_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_senha
	from	pls_estipulante_web
	where	length(ds_senha) <> 32;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		ds_senha
	from	pls_usuario_web
	where	length(ds_senha) <> 32;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_estipulante_w,
	ds_senha_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	Obter_MD5(ds_senha_w)
	into STRICT	ds_senha_nova_w
	;

	update	pls_estipulante_web
	set	ds_senha	= ds_senha_nova_w
	where	nr_sequencia	= nr_seq_estipulante_w;

	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_prestador_w,
	ds_senha_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	select	Obter_MD5(ds_senha_w)
	into STRICT	ds_senha_nova_w
	;

	update	pls_usuario_web
	set	ds_senha	= ds_senha_nova_w
	where	nr_sequencia	= nr_seq_prestador_w;

	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
