SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_atualizar_liber_per () AS $body$
BEGIN

delete     FROM pls_cad_regra_item_perfil
where      nr_seq_item in (     SELECT  nr_sequencia
				from    pls_cadastro_regra_item
				where   coalesce(nr_seq_superior::text, '') = ''
				and     ie_liberacao_perfil = 'S'
				and     nr_sequencia in (       SELECT  nr_seq_superior
								from    pls_cadastro_regra_item
								where   (nr_seq_superior IS NOT NULL AND nr_seq_superior::text <> '')));

update	pls_cadastro_regra_item
set	ie_liberacao_perfil	= 'N'
where	coalesce(nr_seq_superior::text, '') = ''
and	ie_liberacao_perfil	= 'S'
and	nr_sequencia	in (	SELECT	nr_seq_superior
				from	pls_cadastro_regra_item
				where	(nr_seq_superior IS NOT NULL AND nr_seq_superior::text <> ''));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
