SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_atualizar_mes_reaj () AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_contrato,
		cd_estabelecimento
	from	pls_contrato
	where	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

C02 CURSOR FOR
	SELECT	nr_sequencia nr_seq_intercambio,
		cd_estabelecimento
	from	pls_intercambio
	where	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
	
C03 CURSOR FOR
	SELECT	nr_sequencia nr_seq_grupo_contrato,
		(to_char(dt_reajuste, 'mm'))::numeric  nr_mes_reajuste
	from	pls_grupo_contrato
	where	(dt_reajuste IS NOT NULL AND dt_reajuste::text <> '')
	and	coalesce(nr_mes_reajuste::text, '') = ''
	and	ie_tipo_relacionamento in (1,2,3);

C04 CURSOR FOR
	SELECT	nr_sequencia nr_seq_grupo_interc,
		(to_char(dt_reajuste, 'mm'))::numeric  nr_mes_reajuste
	from	pls_regra_grupo_inter
	where	(dt_reajuste IS NOT NULL AND dt_reajuste::text <> '')
	and	coalesce(nr_mes_reajuste::text, '') = '';

BEGIN

CALL wheb_usuario_pck.set_ie_executar_trigger('N');

update	pls_contrato
set	nr_mes_reajuste  = NULL,
	nr_seq_grupo_reajuste  = NULL
where	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

update	pls_intercambio
set	nr_mes_reajuste  = NULL,
	nr_seq_grupo_reajuste  = NULL
where	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

update	pls_segurado
set	nr_mes_reajuste  = NULL
where	coalesce(dt_cancelamento::text, '') = '';

update	pls_sca_vinculo a
set	a.nr_mes_reajuste  = NULL
where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	exists (SELECT	1
		from	pls_plano b
		where	b.nr_sequencia = a.nr_seq_plano
		and	b.ie_permite_reajuste = 'S')
and	(a.nr_seq_segurado IS NOT NULL AND a.nr_seq_segurado::text <> '');

for r_c03_w in c03 loop
	begin
	
	update	pls_grupo_contrato
	set	nr_mes_reajuste = r_c03_w.nr_mes_reajuste,
		nm_usuario	= 'TasyAttMesReaj',
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia 	= r_c03_w.nr_seq_grupo_contrato;
	
	end;
end loop;

for r_c04_w in c04 loop
	begin
	
	update	pls_regra_grupo_inter
	set	nr_mes_reajuste = r_c04_w.nr_mes_reajuste,
		nm_usuario	= 'TasyAttMesReaj',
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia = r_c04_w.nr_seq_grupo_interc;
	
	end;
end loop;

for r_c01_w in c01 loop
	begin
	
	CALL pls_atualizar_mes_reaj_pck.aprovar_contrato(r_c01_w.nr_seq_contrato, 'TasyAttMesReaj', r_c01_w.cd_estabelecimento);
	
	end;
end loop;

for r_c02_w in c02 loop
	begin
	
	CALL pls_atualizar_mes_reaj_pck.aprovar_contrato_intercambio(r_c02_w.nr_seq_intercambio, 'TasyAttMesReaj', r_c02_w.cd_estabelecimento);
	
	end;
end loop;

CALL wheb_usuario_pck.set_ie_executar_trigger('S');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_atualizar_mes_reaj () FROM PUBLIC;

