	update	pls_grupo_contrato_web
	set	dt_alteracao_senha = coalesce(dt_atualizacao, clock_timestamp())
	where 	coalesce(dt_alteracao_senha::text, '') = '';

	update	pls_segurado_web
	set	dt_alteracao_senha = coalesce(dt_atualizacao, clock_timestamp())
	where 	coalesce(dt_alteracao_senha::text, '') = '';

	update	pls_cooperado_web
	set	dt_alteracao_senha = coalesce(dt_atualizacao, clock_timestamp())
	where 	coalesce(dt_alteracao_senha::text, '') = '';

