CREATE OR REPLACE PROCEDURE pls_baca_atualiza_dados_conta ( dt_periodo_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_w		bigint;
nr_seq_plano_conta_w	bigint;
ie_tipo_plano_conta_w	varchar(3);
ie_tipo_benef_conta_w	varchar(3);
ie_tipo_contratacao_w	varchar(2);
ie_participacao_w	varchar(1);
ie_tipo_plano_w		varchar(3);
nr_seq_plano_w		bigint;
ie_tipo_beneficiario_w	varchar(3);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_plano,
		ie_tipo_plano,
		ie_tipo_beneficiario
	from	pls_conta
	where	((coalesce(nr_seq_plano::text, '') = '') or (coalesce(ie_tipo_plano::text, '') = '') or (coalesce(ie_tipo_beneficiario::text, '') = ''))
	and	coalesce(dt_autorizacao,'31/12/2008')	> dt_periodo_p
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_w,
	nr_seq_plano_conta_w,
	ie_tipo_plano_conta_w,
	ie_tipo_benef_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	begin
	select	c.nr_sequencia,
		c.ie_tipo_contratacao,
		c.ie_participacao,
		d.ie_tipo_beneficiario
	into STRICT	nr_seq_plano_w,
		ie_tipo_contratacao_w,
		ie_participacao_w,
		ie_tipo_beneficiario_w
	from	pls_contrato	d,
		pls_plano	c,
		pls_segurado	b,
		pls_conta	a
	where	a.nr_sequencia		= nr_seq_conta_w
	and	a.nr_seq_segurado	= b.nr_sequencia
	and	b.nr_seq_plano		= c.nr_sequencia
	and	b.nr_seq_contrato	= d.nr_sequencia;

	/* Obter o tipo do plano do beneficiário - Domínio 2213 */

	if (ie_tipo_contratacao_w	= 'I') then
		ie_tipo_plano_w	:= 'IF';
	elsif (ie_participacao_w	= 'S') then
		ie_tipo_plano_w	:= 'CSP';
	elsif (ie_participacao_w	= 'C') then
		ie_tipo_plano_w	:= 'CCP';
	end if;

	update	pls_conta
	set	nr_seq_plano		= coalesce(nr_seq_plano_conta_w, nr_seq_plano_w),
		ie_tipo_plano		= coalesce(ie_tipo_plano_conta_w, ie_tipo_plano_w),
		ie_tipo_beneficiario	= coalesce(ie_tipo_benef_conta_w, ie_tipo_beneficiario_w)
	where	nr_sequencia	= nr_seq_conta_w;
	exception
		when others then
		ie_tipo_plano_w	:= '';
	end;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
