CREATE OR REPLACE PROCEDURE pls_baca_atualiza_diarias_sip ( nr_seq_lote_sip_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;
nr_seq_conta_w		bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	pls_conta_proc		c,
		pls_conta		b,
		pls_protocolo_conta	a
	where	a.nr_sequencia	= b.nr_seq_protocolo
	and	b.nr_sequencia	= c.nr_seq_conta
	and	a.ie_status	= 3
	and	a.dt_mes_competencia between dt_periodo_inicial_w and dt_periodo_final_w;

BEGIN

begin
select	dt_periodo_inicial,
	coalesce(dt_periodo_final, clock_timestamp())
into STRICT	dt_periodo_inicial_w,
	dt_periodo_final_w
from	pls_lote_sip
where	nr_sequencia	= nr_seq_lote_sip_p;
exception
	when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266994,'NR_SEQ_LOTE_SIP=' || nr_seq_lote_sip_p);
end;

open C01;
loop
fetch C01 into
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_gerar_diarias_sip_conta(nr_seq_conta_w, nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
