CREATE OR REPLACE PROCEDURE pls_baca_atualiza_internacao ( dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_w		bigint;
ie_internado_w		varchar(1)	:= 'S';

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta
	where	coalesce(dt_autorizacao,clock_timestamp())	> dt_referencia_p
	order by nr_sequencia;


BEGIN

delete from log_tasy where cd_log = 1201;

open C01;
loop
fetch C01 into
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_internado_w	:= pls_obter_se_internado(nr_seq_conta_w, 'C');
	if (ie_internado_w	= 'S') then
		CALL pls_gerar_benef_internado(nr_seq_conta_w, nm_usuario_p);
	end if;
	--insert into log_tasy values(sysdate, nm_usuario_p, 1201, nr_seq_conta_w); commit;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
