SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_atualiza_tp_cobr_fat () AS $body$
BEGIN

update	pls_fatura_proc a
set	a.ie_tipo_cobranca	= (	SELECT	i.ie_tipo_cobranca
					from	pls_fatura_proc i
					where	i.nr_sequencia = a.nr_seq_fat_proc_cancel)
where	coalesce(a.ie_tipo_cobranca::text, '') = ''
and	a.dt_atualizacao_nrec > to_date('01/01/2016');

update	pls_fatura_mat a
set	a.ie_tipo_cobranca	= (	SELECT	i.ie_tipo_cobranca
					from	pls_fatura_mat i
					where	i.nr_sequencia = a.nr_seq_fat_mat_cancel)
where	coalesce(a.ie_tipo_cobranca::text, '') = ''
and	a.dt_atualizacao_nrec > to_date('01/01/2016');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
