CREATE OR REPLACE PROCEDURE pls_baca_atualiza_valor_benef ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_w		bigint;
vl_total_beneficiario_w	double precision;
vl_beneficiario_w	double precision;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		coalesce(a.vl_total_beneficiario,0)
	from	pls_protocolo_conta	b,
		pls_conta		a
	where	a.nr_seq_protocolo	= b.nr_sequencia
	and	b.dt_mes_competencia	> '31/12/2009';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_w,
	vl_total_beneficiario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(sum(vl_beneficiario),0)
	into STRICT	vl_beneficiario_w
	from	pls_conta_proc
	where	nr_seq_conta	= nr_seq_conta_w;

	if (vl_beneficiario_w <> vl_total_beneficiario_w) then
		update	pls_conta
		set	vl_total_beneficiario	= vl_beneficiario_w
		where	nr_sequencia		= nr_seq_conta_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
