CREATE OR REPLACE PROCEDURE pls_baca_atu_dt_refer_lanc () AS $body$
DECLARE


nr_seq_vendedor_w	pls_vendedor_lanc.nr_seq_vendedor%type;
nr_seq_repasse_lanc_w	pls_vendedor_lanc.nr_seq_repasse_lanc%type;
ie_origem_lancamento_w  pls_vendedor_lanc.ie_origem_lancamento%type;
dt_lancamento_w  pls_vendedor_lanc.dt_lancamento%type;

C01 CURSOR FOR
	SELECT	nr_seq_vendedor,
	        nr_seq_repasse_lanc,
	        ie_origem_lancamento
	from	pls_vendedor_lanc
	where 	ie_origem_lancamento <> 'M';

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_vendedor_w,
	nr_seq_repasse_lanc_w,
	ie_origem_lancamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	dt_lancamento_w := pls_obter_dt_refer_lanc_adic( nr_seq_vendedor_w, nr_seq_repasse_lanc_w, ie_origem_lancamento_w );

	update 	pls_vendedor_lanc
	set	dt_lancamento = dt_lancamento_w
	where 	nr_seq_vendedor = nr_seq_vendedor_w
	and 	nr_seq_repasse_lanc = nr_seq_repasse_lanc_w
	and 	ie_origem_lancamento = ie_origem_lancamento_w;

end loop;
