		b.qt_solicitada_imp 
	from	pls_guia_plano_imp a, 
		pls_guia_plano_proc b 
	where	a.nr_seq_guia_plano = b.nr_seq_guia 
	and	a.nm_usuario_nrec = 'TASY_IMP';

