CREATE OR REPLACE PROCEDURE pls_baca_carteira_benef () AS $body$
DECLARE

 
nr_seq_carteira_emissao_w	bigint;
ie_situacao_w				varchar(5);

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_carteira_emissao;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_carteira_emissao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	 
	select	substr(pls_obter_dados_carteira(nr_seq_seg_carteira,null,'T'),1,255) 
	into STRICT	ie_situacao_w 
	from	pls_carteira_emissao 
	where	nr_sequencia = nr_seq_carteira_emissao_w;
	 
	update	pls_carteira_emissao 
	set		ie_situacao = ie_situacao_w 
	where	nr_sequencia = nr_seq_carteira_emissao_w;
	 
	if (ie_situacao_w = 'P') then 
		update	pls_carteira_emissao 
		set		dt_recebimento  = NULL, 
				nm_usuario_recebimento  = NULL 
		where	nr_sequencia = nr_seq_carteira_emissao_w;
	end if;
	 
end loop;
close C01;
 
commit;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
