C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_motivo_glosa_aceita,
		cd_centro_custo
	from	pls_discussao_proc
	where	(cd_centro_custo IS NOT NULL AND cd_centro_custo::text <> '')
	order by nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_motivo_glosa_aceita,
		cd_centro_custo
	from	pls_discussao_mat
	where	(cd_centro_custo IS NOT NULL AND cd_centro_custo::text <> '')
