CREATE OR REPLACE PROCEDURE pls_baca_conta_guia ( nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.qt_procedimento
	from	pls_conta_proc a,
		pls_conta b
	where	a.nr_seq_conta	= b.nr_sequencia
	and	(b.cd_guia IS NOT NULL AND b.cd_guia::text <> '')
	and	trunc(coalesce(a.dt_procedimento,clock_timestamp()),'month') >= trunc(add_months(clock_timestamp(),-6),'month')
	and	not exists (	SELECT	1
				from	pls_conta_autor	x
				where	x.nr_seq_conta = a.nr_seq_conta);

nr_seq_conta_proc_w	bigint;
qt_procedimento_w	bigint;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_proc_w,
	qt_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_atualiza_conta_autor(nr_seq_conta_proc_w, 'L', 'P', qt_procedimento_w, nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
