SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_conta_tit_mensalidade () AS $body$
DECLARE


cd_conta_deb_w			varchar(20);
nr_seq_item_mens_w		bigint;
nr_seq_mensalidade_w		bigint;
nr_seq_nota_fiscal_w		bigint;
nr_seq_item_nf_w		bigint;
nr_item_nf_w			integer;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ie_tipo_item_w			varchar(2);
nr_titulo_w			bigint;
cd_conta_financ_w		bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		c.nr_titulo
	from	pls_lote_mensalidade a,
		pls_mensalidade b,
		titulo_receber c
	where	a.nr_sequencia	= b.nr_seq_lote
	and	b.nr_sequencia	= c.nr_seq_mensalidade
	and	exists (	SELECT	1
				from	titulo_receber_classif x
				where	c.nr_titulo     = x.nr_titulo
				and	coalesce(x.cd_conta_contabil::text, '') = '');

C02 CURSOR FOR
	SELECT	nr_sequencia,
		nr_item_nf,
		cd_procedimento,
		ie_origem_proced,
		nr_seq_conta_financ
	from	nota_fiscal_item
	where	nr_sequencia	= nr_seq_nota_fiscal_w;

C03 CURSOR FOR
	SELECT	b.cd_conta_deb
	from	pls_mensalidade_segurado a,
		pls_mensalidade_seg_item b
	where	a.nr_sequencia	= b.nr_seq_mensalidade_seg
	and	a.nr_seq_mensalidade	= nr_seq_mensalidade_w
	and	b.ie_tipo_item 	= ie_tipo_item_w
	and	ie_tipo_item_w	<> 3
	
UNION

	SELECT	max(c.cd_conta_deb)
	from	pls_mensalidade_segurado a,
		pls_mensalidade_seg_item b,
		pls_conta_coparticipacao c
	where	a.nr_sequencia	= b.nr_seq_mensalidade_seg
	and	b.nr_seq_conta	= c.nr_seq_conta
	and	a.nr_seq_mensalidade	= nr_seq_mensalidade_w
	and	b.ie_tipo_item	= ie_tipo_item_w
	and	ie_tipo_item_w	= 3;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_mensalidade_w,
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nr_sequencia
	into STRICT	nr_seq_nota_fiscal_w
	from	nota_fiscal
	where	nr_seq_mensalidade	= nr_seq_mensalidade_w;

	open C02;
	loop
	fetch C02 into
		nr_seq_item_nf_w,
		nr_item_nf_w,
		cd_procedimento_w,
		ie_origem_proced_w,
		cd_conta_financ_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	ie_tipo_item
		into STRICT	ie_tipo_item_w
		from	pls_tipo_item_mens
		where	cd_procedimento	= cd_procedimento_w
		and	ie_origem_proced = ie_origem_proced_w;

		open C03;
		loop
		fetch C03 into
			cd_conta_deb_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin
			if (coalesce(cd_conta_deb_w,'0') <> '0') then
				update	nota_fiscal_item
				set	cd_conta_contabil	= cd_conta_deb_w,
					nm_usuario	= 'TASY'
				where	nr_sequencia	= nr_seq_item_nf_w
				and	nr_item_nf	= nr_item_nf_w;

				update	titulo_receber_classif
				set	cd_conta_contabil = cd_conta_deb_w,
					nm_usuario	= 'TASY'
				where	nr_titulo	= nr_titulo_w
				and	((cd_conta_financ	= cd_conta_financ_w)
				or (coalesce(cd_conta_financ_w::text, '') = '' and coalesce(cd_conta_financ::text, '') = ''));
			end if;

			end;
		end loop;
		close C03;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_conta_tit_mensalidade () FROM PUBLIC;

