SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_crm_regras_oc () AS $body$
DECLARE


nr_seq_regra_oc_w	bigint;
nr_crm_exec_w		varchar(20);
nr_crm_solic_w		varchar(20);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		substr(obter_crm_medico(cd_medico_executor),1,255),
		substr(obter_crm_medico(cd_medico_executor_solic),1,255)
	from	pls_ocorrencia_regra
	where	(cd_medico_executor IS NOT NULL AND cd_medico_executor::text <> '')
	or	(cd_medico_executor_solic IS NOT NULL AND cd_medico_executor_solic::text <> '')
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_oc_w,
	nr_crm_exec_w,
	nr_crm_solic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_ocorrencia_regra
	set	nr_crm_executor		= nr_crm_exec_w,
		nr_crm_solicitante	= nr_crm_solic_w
	where	nr_sequencia 		= nr_seq_regra_oc_w;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
