CREATE OR REPLACE PROCEDURE pls_baca_desconto_bonificacao () AS $body$
DECLARE


nr_seq_bonific_regra_w	bigint;
nr_seq_desconto_w	bigint;

ie_demitido_exonerado_w		varchar(10);
ie_tipo_segurado_w		varchar(1);
ie_possui_bonificacao_w		varchar(1);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_desconto
	from	pls_bonificacao_regra
	where	(nr_seq_desconto IS NOT NULL AND nr_seq_desconto::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_bonific_regra_w,
	nr_seq_desconto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(ie_demitido_exonerado),
		max(ie_tipo_segurado),
		max(ie_possui_bonificacao)
	into STRICT	ie_demitido_exonerado_w,
		ie_tipo_segurado_w,
		ie_possui_bonificacao_w
	from	pls_preco_regra
	where	nr_seq_regra	= nr_seq_desconto_w;

	update	pls_bonificacao_regra
	set	ie_demitido_exonerado	= ie_demitido_exonerado_w,
		ie_tipo_segurado	= ie_tipo_segurado_w,
		ie_possui_bonificacao	= ie_possui_bonificacao_w
	where	nr_sequencia	= nr_seq_bonific_regra_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
