SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_dt_contabil_item_resc () AS $body$
DECLARE


dt_contabil_w		pls_solic_resc_fin_item.dt_contabil%type;	
dt_solicitacao_w	timestamp;

c01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_item_resc,
		dt_referencia,
		nr_seq_solic_resc_fin
	from	pls_solic_resc_fin_item
	where	coalesce(dt_contabil::text, '') = '';

BEGIN

for r_c01_w in c01 loop
	begin
	dt_contabil_w	:= null;
	
	if (r_c01_w.nr_seq_solic_resc_fin IS NOT NULL AND r_c01_w.nr_seq_solic_resc_fin::text <> '') then
		select	coalesce(b.dt_rescisao,b.dt_solicitacao)
		into STRICT	dt_solicitacao_w
		from	pls_solic_rescisao_fin a,
			pls_solicitacao_rescisao b
		where	b.nr_sequencia = a.nr_seq_solicitacao
		and	a.nr_sequencia = r_c01_w.nr_seq_solic_resc_fin;
		
		if (r_c01_w.dt_referencia > dt_solicitacao_w) then
			dt_contabil_w	:= r_c01_w.dt_referencia;
		else
			dt_contabil_w	:= dt_solicitacao_w;
		end if;
		
		update	pls_solic_resc_fin_item
		set	nm_usuario = 'OS2111797',
			dt_atualizacao = clock_timestamp(),
			dt_contabil = dt_contabil_w
		where	nr_sequencia = r_c01_w.nr_seq_item_resc;
	end if;
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_dt_contabil_item_resc () FROM PUBLIC;

