SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_dt_vigencia () AS $body$
DECLARE


nr_seq_segurado_w		bigint;
dt_reativacao_w		timestamp;
nr_seq_contrato_w		bigint;
qt_dias_valid_cart_w	integer;
dt_validade_w			timestamp;

C01 CURSOR FOR
	SELECT	a.nr_seq_segurado,
				coalesce(b.dt_reativacao,b.dt_liberacao),
				b.nr_seq_contrato,
				coalesce(qt_dias_valid_cart,0)
	from		pls_segurado_carteira a,
				pls_segurado b,
				pls_contrato c
	where		a.nr_seq_segurado = b.nr_sequencia
	and		b.nr_seq_contrato = c.nr_sequencia
	and		coalesce(a.dt_validade_carteira::text, '') = ''
	and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and		coalesce(qt_dias_valid_cart,0) <> 0;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_segurado_w,
	dt_reativacao_w,
	nr_seq_contrato_w,
	qt_dias_valid_cart_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	dt_validade_w := (dt_reativacao_w + qt_dias_valid_cart_w);

	update	pls_segurado_carteira
	set		dt_validade_carteira = dt_validade_w
	where		nr_seq_segurado = nr_seq_segurado_w;

end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
