SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_estab_solic_resc () AS $body$
DECLARE


qt_registro_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		(	SELECT	max(x.cd_estabelecimento)
			from	pls_contrato x
			where	x.nr_sequencia	= a.nr_seq_contrato) cd_estabelecimento
	from	pls_solicitacao_rescisao a
	where	coalesce(a.cd_estabelecimento::text, '') = '';

C02 CURSOR FOR
	SELECT	a.nr_sequencia,
		(	SELECT	max(x.cd_estabelecimento)
			from	pls_contrato x
			where	x.nr_sequencia	= a.nr_seq_contrato) cd_estabelecimento
	from	pls_lote_inclusao_benef a
	where	coalesce(a.cd_estabelecimento::text, '') = '';
BEGIN

for r_c01_w in C01 loop
	begin
	update	pls_solicitacao_rescisao
	set	cd_estabelecimento	= coalesce(r_c01_w.cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento)
	where	nr_sequencia		= r_c01_w.nr_sequencia;
	end;
end loop;

select	count(1)
into STRICT	qt_registro_w
from	user_tab_columns
where	table_name 	= 'PLS_SOLICITACAO_RESCISAO'
and	nullable 	= 'Y'
and	column_name	= 'CD_ESTABELECIMENTO';

if (qt_registro_w > 0) then
	CALL exec_sql_dinamico('BACA', 'alter table PLS_SOLICITACAO_RESCISAO modify CD_ESTABELECIMENTO not null');
end if;

for r_c02_w in C02 loop
	begin
	update	pls_lote_inclusao_benef
	set	cd_estabelecimento	= coalesce(r_c02_w.cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento)
	where	nr_sequencia		= r_c02_w.nr_sequencia;
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
