SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_gerar_tx_pos_estab () AS $body$
DECLARE


nr_seq_conta_w		pls_conta.nr_sequencia%type;
nr_seq_contrato_w	pls_contrato.nr_sequencia%type;
nr_seq_intercambio_w	pls_intercambio.nr_sequencia%type;
nr_seq_plano_w		pls_plano.nr_sequencia%type;
dt_atendimento_w	pls_conta.dt_atendimento%type;
qt_registro_w		integer;
nr_seq_congenere_seg_w	pls_congenere.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_seq_contrato,
		b.nr_seq_intercambio,
		--nvl(a.nr_seq_plano,b.nr_seq_plano),
		coalesce(a.nr_seq_plano,pls_obter_produto_benef(b.nr_sequencia, a.dt_atendimento_referencia)),
		coalesce(a.dt_atendimento_referencia,a.dt_atendimento),
		coalesce(b.nr_seq_ops_congenere,b.nr_seq_congenere)
	from	pls_conta	a,
		pls_segurado	b
	where	a.nr_seq_segurado	= b.nr_sequencia
	and	a.ie_status = 'F'
	and	exists (SELECT	1
			from	pls_conta_pos_estabelecido x
			where	x.nr_seq_conta	= a.nr_sequencia
			and	coalesce(x.nr_seq_mensalidade_seg::text, '') = '');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_w,
	nr_seq_contrato_w,
	nr_seq_intercambio_w,
	nr_seq_plano_w,
	dt_atendimento_w,
	nr_seq_congenere_seg_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_conta_pos_estab_taxa a,
		pls_conta_pos_estabelecido b
	where	a.nr_seq_conta_pos_estab = b.nr_sequencia
	and	b.nr_seq_conta = nr_seq_conta_w;

	if (qt_registro_w = 0) then
		CALL pls_gerar_taxa_adm_pos_estab(	nr_seq_conta_w,
						nr_seq_contrato_w,
						nr_seq_intercambio_w,
						nr_seq_plano_w,
						dt_atendimento_w,
						nr_seq_congenere_seg_w,
						'TASY');
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
