CREATE OR REPLACE PROCEDURE pls_baca_gerar_view_val () AS $body$
DECLARE


qt_registro_w	integer;


BEGIN


-- insere ou atualiza um registro para marcar a necessidade de gerar novamente as view
select	count(1)
into STRICT	qt_registro_w
from	pls_regra_preco_controle;

if (qt_registro_w > 0) then
	update	pls_regra_preco_controle set
		ie_gera_novamente = 'S';
else
	insert into pls_regra_preco_controle(ie_gera_novamente) values ('S');
end if;

commit;

-- atualiza as view
CALL pls_cta_consistir_pck.gera_views_valorizacao();

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
