CREATE OR REPLACE PROCEDURE pls_baca_guia_item_conta () AS $body$
DECLARE


qt_commit_w		integer := 0;

c01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_conta,
		nm_usuario
	from	pls_conta
	where	ie_status	!= 'C'
	and	(dt_fechamento_conta IS NOT NULL AND dt_fechamento_conta::text <> '')
	and	(nr_seq_guia IS NOT NULL AND nr_seq_guia::text <> '')
	and	(nr_seq_segurado IS NOT NULL AND nr_seq_segurado::text <> '')
	and	dt_atendimento_referencia >= to_date('01/01/2016');

BEGIN
for r_c01_w in c01 loop
	CALL pls_insere_guia_proc_mat( r_c01_w.nr_seq_conta, r_c01_w.nm_usuario);

	qt_commit_w := qt_commit_w + 1;
	if (qt_commit_w = 500) then
		qt_commit_w := 1;
		commit;
	end if;
end loop;

commit;

end;
