CREATE OR REPLACE PROCEDURE pls_baca_inf_fin_mens ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_mensalidade_w	bigint;
nr_seq_pagador_w	bigint;
dt_referencia_w		timestamp;
nr_seq_pagador_fin_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_pagador,
		dt_referencia
	from	pls_mensalidade
	where	coalesce(nr_seq_pagador_fin::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_mensalidade_w,
	nr_seq_pagador_w,
	dt_referencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_pagador_fin_w
	from	pls_contrato_pagador_fin
	where	nr_seq_pagador	= nr_seq_pagador_w
	and	dt_referencia_w between trunc(dt_inicio_vigencia,'month') and fim_dia(coalesce(dt_fim_vigencia,dt_referencia_w));

	if (coalesce(nr_seq_pagador_fin_w,0) <> 0) then
		begin
		update	pls_mensalidade
		set	nr_seq_pagador_fin	= nr_seq_pagador_fin_w
		where	nr_sequencia		= nr_seq_mensalidade_w;
		exception
		when others then
			null;
		end;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
