CREATE OR REPLACE PROCEDURE pls_baca_lote_notificacao ( nm_usuario_p text) AS $body$
DECLARE



vl_rescisao_w			double precision;
qt_pagador_w			integer;
qt_benef_rescindido_w		integer;
vl_total_rescisao_w		double precision;
qt_total_pagador_w		integer;
qt_total_rescindido_w		integer;
nr_sequencia_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		vl_rescisao,
		qt_pagador,
		qt_benef_rescindido
	from	pls_notificacao_lote;


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w,
	vl_rescisao_w,
	qt_pagador_w,
	qt_benef_rescindido_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(vl_rescisao_w::text, '') = '') then
		select	sum(pls_obter_dados_benef_rescisao(nr_sequencia, 'V'))
		into STRICT	vl_total_rescisao_w
		from	pls_notificacao_pagador
		where	nr_seq_lote	= nr_sequencia_w;

		update  pls_notificacao_lote
		set	vl_rescisao	= vl_total_rescisao_w
		where	nr_sequencia	= nr_sequencia_w;
	end if;

	if (coalesce(qt_pagador_w::text, '') = '') then
		select	count(*)
		into STRICT	qt_total_pagador_w
		from	pls_notificacao_pagador
		where	nr_seq_lote	= nr_sequencia_w;

		update  pls_notificacao_lote
		set	qt_pagador	= qt_total_pagador_w
		where	nr_sequencia	= nr_sequencia_w;
	end if;

	if (coalesce(qt_benef_rescindido_w::text, '') = '') then
		select	sum(pls_obter_dados_benef_rescisao(nr_sequencia, 'Q'))
		into STRICT	qt_total_rescindido_w
		from	pls_notificacao_pagador
		where	nr_seq_lote	= nr_sequencia_w;

		update  pls_notificacao_lote
		set	qt_benef_rescindido	= qt_total_rescindido_w
		where	nr_sequencia	= nr_sequencia_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
