SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_medico_exec_prest () AS $body$
DECLARE



nr_seq_prestador_w		bigint;
cd_pessoa_fisica_w		varchar(15);
nr_seq_executor_w		bigint;
qt_registro_w			bigint	:= 0;

C01 CURSOR FOR
	SELECT	b.nr_seq_prestador,
		a.cd_pessoa_fisica,
		a.nr_sequencia
	from	pls_prestador_medico		b,
		pls_regra_exec_medic_prest	a
	where	a.cd_pessoa_fisica		= b.cd_medico
	and	a.ie_responsavel_exec		= 'S'
	and	coalesce(a.nr_seq_prestador::text, '') = ''
	order by b.nr_sequencia asc;

BEGIN

begin
	open C01;
	loop
	fetch C01 into
		nr_seq_prestador_w,
		cd_pessoa_fisica_w,
		nr_seq_executor_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			update 	pls_regra_exec_medic_prest
			set	nr_seq_prestador = nr_seq_prestador_w
			where	nr_sequencia = nr_seq_executor_w;
		end;
	end loop;
	close C01;


	delete from pls_regra_exec_medic_prest
	where	nr_sequencia in (SELECT	nr_sequencia
				 from	pls_regra_exec_medic_prest
				 where	coalesce(nr_seq_prestador::text, '') = '');
exception
when others then
	qt_registro_w := 0;
end;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
