CREATE OR REPLACE PROCEDURE pls_baca_nivel_liberacao () AS $body$
DECLARE


nr_nivel_liberacao_w		bigint;
qt_registro_w			bigint;
nr_seq_nivel_lib_w		bigint;

C01 CURSOR FOR
	SELECT	nr_nivel_liberacao
	from	pls_ocorrencia
	where	(nr_nivel_liberacao IS NOT NULL AND nr_nivel_liberacao::text <> '')
	group by nr_nivel_liberacao;

C02 CURSOR FOR
	SELECT	nr_nivel_liberacao
	from	pls_grupo_auditor
	where	(nr_nivel_liberacao IS NOT NULL AND nr_nivel_liberacao::text <> '')
	group by nr_nivel_liberacao;

C03 CURSOR FOR
	SELECT	nr_nivel_liberacao
	from	pls_membro_grupo_aud
	where	(nr_nivel_liberacao IS NOT NULL AND nr_nivel_liberacao::text <> '')
	group by nr_nivel_liberacao;


BEGIN

open C01;
loop
fetch C01 into
	nr_nivel_liberacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_nivel_liberacao
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;

	if (qt_registro_w	= 0) then
		insert into pls_nivel_liberacao(nr_sequencia, cd_estabelecimento, nr_nivel_liberacao,
			ds_nivel_liberacao, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec)
		values (	nextval('pls_nivel_liberacao_seq'), 1, nr_nivel_liberacao_w,
			nr_nivel_liberacao_w, clock_timestamp(), 'Tasy',
			clock_timestamp(), 'Tasy');
	end if;

	select	max(nr_sequencia)
	into STRICT	nr_seq_nivel_lib_w
	from	pls_nivel_liberacao
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;

	update	pls_ocorrencia
	set	nr_seq_nivel_lib	= nr_seq_nivel_lib_w
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_nivel_liberacao_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_nivel_liberacao
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;

	if (qt_registro_w	= 0) then
		insert into pls_nivel_liberacao(nr_sequencia, cd_estabelecimento, nr_nivel_liberacao,
			ds_nivel_liberacao, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec)
		values (	nextval('pls_nivel_liberacao_seq'), 1, nr_nivel_liberacao_w,
			nr_nivel_liberacao_w, clock_timestamp(), 'Tasy',
			clock_timestamp(), 'Tasy');
	end if;

	select	max(nr_sequencia)
	into STRICT	nr_seq_nivel_lib_w
	from	pls_nivel_liberacao
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;

	update	pls_grupo_auditor
	set	nr_seq_nivel_lib	= nr_seq_nivel_lib_w
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;
	end;
end loop;
close C02;

open C03;
loop
fetch C03 into
	nr_nivel_liberacao_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_nivel_liberacao
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;

	if (qt_registro_w	= 0) then
		insert into pls_nivel_liberacao(nr_sequencia, cd_estabelecimento, nr_nivel_liberacao,
			ds_nivel_liberacao, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec)
		values (	nextval('pls_nivel_liberacao_seq'), 1, nr_nivel_liberacao_w,
			nr_nivel_liberacao_w, clock_timestamp(), 'Tasy',
			clock_timestamp(), 'Tasy');
	end if;

	select	max(nr_sequencia)
	into STRICT	nr_seq_nivel_lib_w
	from	pls_nivel_liberacao
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;

	update	pls_membro_grupo_aud
	set	nr_seq_nivel_lib	= nr_seq_nivel_lib_w
	where	nr_nivel_liberacao	= nr_nivel_liberacao_w;
	end;
end loop;
close C03;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
