SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_numero_lote_contabil () AS $body$
DECLARE

 
nr_titulo_w			bigint;
nr_seq_baixa_w			bigint;
nr_lote_contab_antecip_w	bigint;
nr_lote_contab_pro_rata_w	bigint;
qt_registros_w			bigint	:= 0;

C01 CURSOR FOR 
	SELECT	nr_titulo, 
		nr_sequencia, 
		nr_lote_contab_antecip, 
		nr_lote_contab_pro_rata 
	from	titulo_receber_liq 
	where (coalesce(nr_lote_contab_antecip::text, '') = '') or (coalesce(nr_lote_contab_pro_rata::text, '') = '');


BEGIN 
open C01;
loop 
fetch C01 into	 
	nr_titulo_w, 
	nr_seq_baixa_w, 
	nr_lote_contab_antecip_w, 
	nr_lote_contab_pro_rata_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (coalesce(nr_lote_contab_antecip_w::text, '') = '') then 
		update	titulo_receber_liq 
		set	nr_lote_contab_antecip	= 0 
		where	nr_sequencia	= nr_seq_baixa_w 
		and	nr_titulo	= nr_titulo_w;
	end if;
	 
	if (coalesce(nr_lote_contab_pro_rata_w::text, '') = '') then 
		update	titulo_receber_liq 
		set	nr_lote_contab_pro_rata	= 0 
		where	nr_sequencia	= nr_seq_baixa_w 
		and	nr_titulo	= nr_titulo_w;
	end if;
	 
	qt_registros_w	:= qt_registros_w + 1;
	 
	if (qt_registros_w = 300) then 
		qt_registros_w	:= 0;
		commit;
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
