SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_pagador_congenere (nm_usuario_p text) AS $body$
DECLARE


cd_cgc_w				varchar(14);
nr_seq_congenere_w			bigint;
nr_seq_cong_resp_financ_w		bigint;
qt_pagador_congenere_w			integer;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.cd_cgc
	from	pls_congenere a
	where	ie_tipo_congenere = 'CO';



BEGIN
open C01;
loop
fetch C01 into
	nr_seq_congenere_w,
	cd_cgc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(a.nr_seq_cong_resp_financ)
	into STRICT	nr_seq_cong_resp_financ_w
	from	pls_congenere_resp_financ a
	where	a.nr_seq_congenere = nr_seq_congenere_w;

	select	count(*)
	into STRICT	qt_pagador_congenere_w
	from	pls_contrato_pagador
	where	nr_seq_congenere = nr_seq_congenere_w;

	if (qt_pagador_congenere_w = 0) then
		if (nr_seq_cong_resp_financ_w IS NOT NULL AND nr_seq_cong_resp_financ_w::text <> '') then
			select	max(a.cd_cgc)
			into STRICT	cd_cgc_w
			from	pls_congenere a
			where	nr_sequencia = nr_seq_cong_resp_financ_w;

			insert into pls_contrato_pagador(nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				ie_tipo_pagador,
				ie_calc_primeira_mens,
				ie_endereco_boleto,
				cd_cgc,
				nr_seq_congenere)
			values (nextval('pls_contrato_pagador_seq'),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				'P',
				'N',
				'PJ',
				cd_cgc_w,
				nr_seq_cong_resp_financ_w);
		else
			insert into pls_contrato_pagador(nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				ie_tipo_pagador,
				ie_calc_primeira_mens,
				ie_endereco_boleto,
				cd_cgc,
				nr_seq_congenere)
			values (nextval('pls_contrato_pagador_seq'),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				'P',
				'N',
				'PJ',
				cd_cgc_w,
				nr_seq_congenere_w);
		end if;
	end if;
	end;
end loop;
close C01;

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
