CREATE OR REPLACE PROCEDURE pls_baca_pagamento_conta () AS $body$
DECLARE


nr_seq_conta_w		bigint;
nr_seq_protocolo_w	bigint;
nr_seq_prestador_exec_w	bigint;
nr_seq_prot_conta_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_protocolo,
		nr_seq_prestador_exec
	from	pls_conta
	where	coalesce(nr_seq_prot_conta::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_w,
	nr_seq_protocolo_w,
	nr_seq_prestador_exec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_prot_conta_w
	from	pls_prot_conta_titulo
	where	nr_seq_protocolo	= nr_seq_protocolo_w
	and	nr_seq_prestador	= nr_seq_prestador_exec_w;

	if (coalesce(nr_seq_prot_conta_w,0) <> 0) then
		update	pls_conta
		set	nr_seq_prot_conta	= nr_seq_prot_conta_w,
			nm_usuario		= 'wheb',
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_conta_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
