CREATE OR REPLACE PROCEDURE pls_baca_param_contabil ( nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ie_esquema_contabil_w	varchar(1);


BEGIN 
 
select	max(ie_esquema_contabil) 
into STRICT	ie_esquema_contabil_w 
from	pls_parametros 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
insert	into	pls_parametro_contabil(	nr_sequencia, cd_estabelecimento, dt_atualizacao, 
		nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, 
		ie_esquema_contabil, ie_provisao_producao) 
	values (	nextval('pls_parametro_contabil_seq'), cd_estabelecimento_p, clock_timestamp(), 
		nm_usuario_p, clock_timestamp(), nm_usuario_p, 
		coalesce(ie_esquema_contabil_w,'N'), 'N');
 
commit;
 
end;
