CREATE OR REPLACE PROCEDURE pls_baca_parcela_repasse_mens () AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: BACA
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_repasse_w	bigint;
nr_seq_mens_seg_w	bigint;
nr_parcela_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_mens_seg
	from	pls_repasse_mens
	where	coalesce(nr_parcela_benef::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_repasse_w,
	nr_seq_mens_seg_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(nr_parcela)
	into STRICT	nr_parcela_w
	from	pls_mensalidade_segurado
	where	nr_sequencia	= nr_seq_mens_seg_w;

	if (nr_parcela_w IS NOT NULL AND nr_parcela_w::text <> '') then
		update	pls_repasse_mens
		set	nr_parcela_benef = nr_parcela_w
		where	nr_sequencia	= nr_seq_repasse_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
