CREATE OR REPLACE PROCEDURE pls_baca_plano_item_mens_reaj () AS $body$
DECLARE


nr_seq_item_w		pls_mensalidade_seg_item.nr_sequencia%type;
nr_seq_plano_w		pls_plano.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_seq_plano
	from	pls_mensalidade_seg_item	a,
		pls_mensalidade_segurado	b
	where	b.nr_sequencia	= a.nr_seq_mensalidade_seg
	and	a.ie_tipo_item in ('4','5','12','25','31')
	and	coalesce(a.nr_seq_plano::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_item_w,
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	pls_mensalidade_seg_item
	set	nr_seq_plano	= nr_seq_plano_w
	where	nr_sequencia	= nr_seq_item_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
