CREATE OR REPLACE PROCEDURE pls_baca_prestador_ie_interc () AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_publica_ans
	from	pls_prestador
	where	coalesce(ie_intercambio::text, '') = ''
	order by 1;


BEGIN

for r_c01_w in C01 loop
	update 	pls_prestador
	set	ie_intercambio 	= r_c01_w.ie_publica_ans
	where	nr_sequencia 	= r_c01_w.nr_sequencia;
end loop;

commit;

end;
