SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_primeira_mens_pag () AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		CASE WHEN(	SELECT	count(1) 			from	pls_mensalidade x 			where	x.nr_seq_pagador = a.nr_sequencia 			and	coalesce(x.ie_cancelamento::text, '') = '')=0 THEN 'N'  ELSE 'S' END  ie_primeira_mensalidade_gerada 
	from	pls_contrato_pagador a;

nr_seq_pagador_w		pls_util_cta_pck.t_number_table;
ie_primeira_mensalidade_g_w	pls_util_cta_pck.t_varchar2_table_1;
x bigint;

BEGIN 
x	:= 0;
for r_c01_w in C01 loop 
	begin 
	nr_seq_pagador_w(x)		:= r_c01_w.nr_sequencia;
	ie_primeira_mensalidade_g_w(x)	:= r_c01_w.ie_primeira_mensalidade_gerada;
	 
	if (x = 500) then 
		forall i in nr_seq_pagador_w.first..nr_seq_pagador_w.last 
			update	pls_contrato_pagador 
			set	ie_primeira_mensalidade_gerada	= ie_primeira_mensalidade_g_w(i) 
			where	nr_sequencia	= nr_seq_pagador_w(i);
		commit;
		 
		nr_seq_pagador_w.delete;
		ie_primeira_mensalidade_g_w.delete;
		x := 0;
	else 
		x	:= x + 1;
	end if;
	 
	end;
end loop;
 
if (nr_seq_pagador_w.count > 0) then 
	forall i in nr_seq_pagador_w.first..nr_seq_pagador_w.last 
		update	pls_contrato_pagador 
		set	ie_primeira_mensalidade_gerada	= ie_primeira_mensalidade_g_w(i) 
		where	nr_sequencia	= nr_seq_pagador_w(i);
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
