SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_proposta_repasse () AS $body$
DECLARE

 
nr_seq_reg_min_w	pls_repasse_mens.nr_sequencia%type;
nr_seq_reg_max_w	pls_repasse_mens.nr_sequencia%type;

nr_seq_reg_ini_w	pls_repasse_mens.nr_sequencia%type;
nr_seq_reg_fim_w	pls_repasse_mens.nr_sequencia%type;
qt_registro_update_w	integer;

tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_proposta_w	pls_util_cta_pck.t_number_table;
ie_finaliza_processo_w	varchar(1);

C01 CURSOR(	nr_seq_reg_ini_pc	pls_repasse_mens.nr_sequencia%type, 
		nr_seq_reg_fim_pc	pls_repasse_mens.nr_sequencia%type) FOR 
	SELECT 	a.nr_sequencia, 
		c.nr_seq_proposta 
	from 	pls_repasse_mens a, 
		pls_segurado b, 
		pls_proposta_beneficiario c 
	where 	coalesce(a.nr_seq_proposta::text, '') = '' 
	and	coalesce(c.nr_seq_proposta,0) > 0 
	and	a.nr_seq_segurado = b.nr_sequencia 
	and   b.nr_seq_pessoa_proposta = c.nr_sequencia 
	and	a.nr_sequencia between nr_seq_reg_ini_pc and nr_seq_reg_fim_pc;

BEGIN
 
qt_registro_update_w := 500;
ie_finaliza_processo_w := 'N';
 
-- busca os limites da tabela 
select	min(nr_sequencia) nr_seq_min, 
	max(nr_sequencia) nr_seq_max 
into STRICT	nr_seq_reg_min_w, 
	nr_seq_reg_max_w 
from 	pls_repasse_mens;
 
-- seta os limites iniciais 
nr_seq_reg_ini_w := nr_seq_reg_min_w;
nr_seq_reg_fim_w := nr_seq_reg_ini_w + qt_registro_update_w;
 
loop 
	tb_nr_sequencia_w.delete;
	 
	open c01(nr_seq_reg_ini_w, nr_seq_reg_fim_w);
	fetch c01 bulk collect into tb_nr_sequencia_w, tb_nr_seq_proposta_w;
	close c01;
	 
	if (tb_nr_sequencia_w.count > 0) then 
		-- manda os dados para atualizar no banco 
		forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last 
			update	pls_repasse_mens set	 
				nr_seq_proposta = tb_nr_seq_proposta_w(i) 
			where 	nr_sequencia	= tb_nr_sequencia_w(i);
		commit;
	end if;	
 
	-- calcula os novos intervalos 
	nr_seq_reg_ini_w := nr_seq_reg_fim_w + 1;
	nr_seq_reg_fim_w := nr_seq_reg_ini_w + qt_registro_update_w;
	 
	-- se o início ainda não ultrassou a última sequencia da tabela executa novamente 
	if (nr_seq_reg_ini_w <= nr_seq_reg_max_w) then 
		ie_finaliza_processo_w := 'N';
	else 
		ie_finaliza_processo_w := 'S';
	end if;
	 
	exit when ie_finaliza_processo_w = 'S';
	 
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
